import { useState, useEffect } from "react"; 
function Login({ onLogin }) { // Accept onLogin prop
 const initialValues = { 
 username: "", 
 password: "", 
 }; 
 const [formValues, setFormValues] = useState(initialValues); 
 const [formErrors, setFormErrors] = useState({}); 
 const [isSubmit, setIsSubmit] = useState(false); 
 const handleChange = (e) => { 
 const { name, value } = e.target; 
 setFormValues({ ...formValues, [name]: value }); 
 }; 
 const handleSubmit = (e) => { 
 e.preventDefault(); 
 setFormErrors(validate(formValues)); 
 setIsSubmit(true); 
 }; 
 useEffect(() => { 
20 
 if (Object.keys(formErrors).length === 0 && isSubmit) { 
 console.log(formValues); 
 onLogin(); // Call the onLogin function to navigate to FoodDisplay
 } 
 }, [formErrors, formValues, isSubmit, onLogin]); // Add onLogin to 
dependencies
 const validate = (values) => { 
 const errors = {}; 
 if (!values.username) { 
 errors.username = "Username is required!"; 
 } 
 if (!values.password) { 
 errors.password = "Password is required"; 
 } else if (values.password.length < 4) { 
 errors.password = "Password must be more than 4 characters"; 
 } else if (values.password.length > 10) { 
 errors.password = "Password cannot exceed more than 10 characters"; 
 } 
 return errors; 
 }; 
 return ( 
 <>
 <div className="container">
 {Object.keys(formErrors).length === 0 && isSubmit ? ( 
 <div className="ui message success">
 Logged in successfully 
 </div>
 ) : ( 
 console.log("Entered Details", formValues) 
 )}
 <form onSubmit={handleSubmit}>
 <h1>Login</h1> {/* Change the header text to Login */}
 <div className="ui divider"></div>
 <div className="ui form">
 <div className="field">
 <label>Username</label>
<input
 type="text"
name="username"
placeholder="Enter your username"
value={formValues.username}
 onChange={handleChange}
 />
 </div>
21 
 <p>{formErrors.username}</p>
 <div className="field">
 <label>Password</label>
<input
 type="password"
name="password"
placeholder="Password"
value={formValues.password}
 onChange={handleChange}
 />
 </div>
<p>{formErrors.password}</p>
 <button className="fluid ui button 
blue">Login</button> {/* Change button text to Login */}
 </div>
 </form>
 <div className="text">
 Don't have an account? <span>Sign Up</span> {/* Adjust 
this to indicate signup */}
 </div>
 </div>
 </>
 ); 
} 
export default Login;
