// About.js
import React from "react";

const About = () => {
  return (
    <div style={styles.container}>
      <div style={styles.overlay}>
        <h1 style={styles.overlayHeading}>Welcome to Foodie Haven</h1>
        <p style={styles.overlayParagraph}>
          Where we make delicious food ordering as easy as a click. Enjoy a wide variety of meals from local favorites to exotic cuisines, all delivered fresh to your doorstep!
        </p>
      </div>
      <div style={styles.card}>
        <h1 style={styles.heading}>About Us</h1>
        <p style={styles.paragraph}>
          At Foodie Haven, we‚Äôre passionate about connecting people with great food. Our goal is to provide a seamless and enjoyable ordering experience, with features designed to cater to all tastes and dietary needs.
        </p>
        <p style={styles.missionHeading}>Our Mission</p>
        <p style={styles.paragraph}>
          To create an inclusive, efficient, and enjoyable dining experience, bringing people closer to their favorite dishes with the utmost convenience and reliability.
        </p>
        <p style={styles.footer}>Happy Dining! üç≤</p>
      </div>
    </div>
  );
};

const styles = {
  container: {
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    height: "100vh",
    backgroundImage: "url('https://png.pngtree.com/background/20230520/original/pngtree-food-background-picture-image_2681326.jpg')", // Replace with your image URL
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    position: "relative",
  },
  overlay: {
    position: "absolute",
    top: "20%",
    textAlign: "center",
    color: "#ffffff",
    padding: "0 20px",
  },
  overlayHeading: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "10px",
  },
  overlayParagraph: {
    fontSize: "1.2rem",
    lineHeight: "1.5",
  },
  card: {
    maxWidth: "600px",
    padding: "40px",
    borderRadius: "10px",
    backgroundColor: "rgba(255, 255, 255, 0.9)", // Semi-transparent for readability
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
    textAlign: "center",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
    zIndex: 1,
  },
  heading: {
    fontSize: "2.5rem",
    color: "#333",
    marginBottom: "20px",
  },
  paragraph: {
    fontSize: "1.1rem",
    color: "#555",
    lineHeight: "1.6",
    marginBottom: "15px",
  },
  missionHeading: {
    fontSize: "1.5rem",
    color: "#333",
    fontWeight: "bold",
    marginTop: "25px",
    marginBottom: "10px",
  },
  footer: {
    fontSize: "1.2rem",
    color: "#007bff",
    marginTop: "20px",
    fontStyle: "italic",
  },
};

export default About;
