import { useState, useEffect } from "react";
import axios from "axios"; // Import axios for making requests

function Login({ onLogin }) {
    const initialValues = {
        username: "",
        password: "",
    };
    const [formValues, setFormValues] = useState(initialValues);
    const [formErrors, setFormErrors] = useState({});
    const [isSubmit, setIsSubmit] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormValues({ ...formValues, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setFormErrors(validate(formValues));
        setIsSubmit(true);

        if (Object.keys(validate(formValues)).length === 0) {
            try {
                const response = await axios.post("http://localhost:5000/login", {
                    username: formValues.username,
                    password: formValues.password,
                });

                if (response.data.token) {
                    localStorage.setItem("token", response.data.token); // Store the token if needed
                    onLogin(); // Navigate to FoodDisplay or other component
                }
            } catch (error) {
                console.error("Login failed", error);
                setFormErrors({ login: "Invalid username or password" });
            }
        }
    };

    useEffect(() => {
        if (Object.keys(formErrors).length === 0 && isSubmit) {
            console.log("Form submitted:", formValues);
        }
    }, [formErrors, formValues, isSubmit]);

    const validate = (values) => {
        const errors = {};
        if (!values.username) {
            errors.username = "Username is required!";
        }
        if (!values.password) {
            errors.password = "Password is required";
        } else if (values.password.length < 4) {
            errors.password = "Password must be more than 4 characters";
        } else if (values.password.length > 10) {
            errors.password = "Password cannot exceed more than 10 characters";
        }
        return errors;
    };

    return (
        <div className="container">
            <style>{/* CSS styles here */}</style>

            {Object.keys(formErrors).length === 0 && isSubmit ? (
                <div className="ui message success">
                    Logged in successfully
                </div>
            ) : (
                <form onSubmit={handleSubmit}>
                    <h1>Login</h1>
                    <div className="ui divider"></div>
                    <div className="ui form">
                        <div className="field">
                            <label>Username</label>
                            <input
                                type="text"
                                name="username"
                                placeholder="Enter your username"
                                value={formValues.username}
                                onChange={handleChange}
                            />
                        </div>
                        <p>{formErrors.username}</p>
                        <div className="field">
                            <label>Password</label>
                            <input
                                type="password"
                                name="password"
                                placeholder="Password"
                                value={formValues.password}
                                onChange={handleChange}
                            />
                        </div>
                        <p>{formErrors.password}</p>
                        <button className="fluid ui button blue">Login</button>
                        <p>{formErrors.login}</p>
                    </div>
                </form>
            )}
            <div className="text">
                Don't have an account? <span>Sign Up</span>
            </div>
        </div>
    );
}

export default Login;
