import React, { useState } from "react";
import "./App.css";
import HomePage from "./HomePage";
import Login from "./Login";
import FoodDisplay from "./FoodDisplay";
import OrderDetails from "./OrderDetails";
import RestaurantOrders from "./RestaurantOrders";
import About from "./About";
import PaymentPage from "./PaymentPage";

function App() {
  const [page, setPage] = useState("home");
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);

  const addToCart = (food) => {
    setCart((prevCart) => [...prevCart, food]);
  };

  const handleLogin = () => {
    setPage("foodDisplay");
  };

  const handleCheckout = () => {
    setPage("payment");
  };

  const handlePaymentSuccess = () => {
    const totalAmount = cart.reduce((total, item) => total + item.price, 0);

    // Save the order details
    const orderDetails = {
      items: cart,
      totalAmount,
    };

    setOrders((prevOrders) => [...prevOrders, orderDetails]);
    setCart([]); // Clear the cart
    setPage("orderDetails"); // Navigate to order details page
  };

  const renderPage = () => {
    switch (page) {
      case "home":
        return (
          <HomePage
            onOrderNow={() => setPage("login")}
            onAboutClick={() => setPage("about")}
          />
        );
      case "login":
        return <Login onLogin={handleLogin} />;
      case "foodDisplay":
        return <FoodDisplay addToCart={addToCart} cart={cart} onCheckout={handleCheckout} />;
      case "payment":
        const totalAmount = cart.reduce((total, item) => total + item.price, 0);
        return <PaymentPage totalAmount={totalAmount} onConfirmPayment={handlePaymentSuccess} />;
      case "orderDetails":
        const lastOrder = orders[orders.length - 1]; // Get the most recent order
        return <OrderDetails order={lastOrder} />;
      case "restaurantOrders":
        return <RestaurantOrders orders={orders} />;
      case "about":
        return <About />;
      default:
        return null;
    }
  };

  return <div className="App">{renderPage()}</div>;
}

export default App;
