import React, { useState } from 'react';
import './App.css';
import HomePage from './HomePage';
import Login from './Login';
import FoodDisplay from './FoodDisplay';
import OrderDetails from './OrderDetails';
import RestaurantOrders from './RestaurantOrders';

function App() {
  const [page, setPage] = useState('home');
  const [cart, setCart] = useState([]);
  const [orders, setOrders] = useState([]);

  const addToCart = (food) => {
    setCart((prevCart) => [...prevCart, food]);
  };

  const handleLogin = () => {
    setPage('foodDisplay');
  };

  const handleCheckout = async () => {
    const totalAmount = cart.reduce((total, item) => total + item.price, 0);

    // Save order to backend
    try {
      const response = await fetch('http://localhost:5000/api/orders/checkout', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ cart, totalAmount }),
      });
      if (response.ok) {
        setOrders((prevOrders) => [...prevOrders, { cart, totalAmount }]);
        setPage('orderDetails');
      }
    } catch (error) {
      console.error('Error during checkout:', error);
    }
  };

  const renderPage = () => {
    switch (page) {
      case 'home':
        return <HomePage onOrderNow={() => setPage('login')} />;
      case 'login':
        return <Login onLogin={handleLogin} />;
      case 'foodDisplay':
        return <FoodDisplay addToCart={addToCart} cart={cart} onCheckout={handleCheckout} />;
      case 'orderDetails':
        const totalAmount = cart.reduce((total, item) => total + item.price, 0);
        return <OrderDetails cart={cart} totalAmount={totalAmount} />;
      case 'restaurantOrders':
        return <RestaurantOrders orders={orders} />;
      default:
        return null;
    }
  };

  return <div className="App">{renderPage()}</div>;
}

export default App;
