// PaymentPage.js
import React from "react";

function PaymentPage({ onPaymentSuccess }) {
  const handlePayment = () => {
    // Simulate a payment processing delay
    setTimeout(() => {
      onPaymentSuccess();
    }, 1000);
  };

  return (
    <div className="payment-page">
      <h2>Payment Page</h2>
      <p>Confirm your payment to proceed with your order.</p>
      <button className="confirm-payment-btn" onClick={handlePayment}>
        Confirm Payment
      </button>
    </div>
  );
}

export default PaymentPage;
