import React from 'react';
import './FoodDisplay.css';
import pizza from './pizza.jpeg';
import burger from './burger.jpeg';
import logo from './logo.png'; 
import sushi from './sushi.jpeg';
import pasta from './pasta.jpeg';
import salad from './salad.jpeg';
import steak from './steak.jpeg';
import tacos from './tacos.jpeg';
import sandwich from './sandwich.jpeg';
import ramen from './ramen.jpeg';
import dumplings from './dumplings.jpeg';
import fries from './fries.jpeg';
import iceCream from './icecream.jpeg';
import curry from './curry.jpeg';
import pancakes from './pancakes.jpeg';
import smoothie from './smoothie.jpeg';

const foodData = [
  { id: 1, name: 'Pizza', price: 10, imageUrl: pizza },
  { id: 2, name: 'Burger', price: 5, imageUrl: burger },
  { id: 3, name: 'Sushi', price: 15, imageUrl: sushi },
  { id: 4, name: 'Pasta', price: 12, imageUrl: pasta },
  { id: 5, name: 'Salad', price: 8, imageUrl: salad },
  { id: 6, name: 'Steak', price: 20, imageUrl: steak },
  { id: 7, name: 'Tacos', price: 9, imageUrl: tacos },
  { id: 8, name: 'Sandwich', price: 6, imageUrl: sandwich },
  { id: 9, name: 'Ramen', price: 14, imageUrl: ramen },
  { id: 10, name: 'Dumplings', price: 10, imageUrl: dumplings },
  { id: 11, name: 'Fries', price: 4, imageUrl: fries },
  { id: 12, name: 'Ice Cream', price: 7, imageUrl: iceCream },
  { id: 13, name: 'Curry', price: 13, imageUrl: curry },
  { id: 14, name: 'Pancakes', price: 8, imageUrl: pancakes },
  { id: 15, name: 'Smoothie', price: 5, imageUrl: smoothie },
];


const FoodDisplay = ({ addToCart, cart, onCheckout }) => {
  const totalAmount = cart.reduce((total, item) => total + item.price, 0);

  return (
    <div className="food-display-container">
    {/* Header Section */}
    <header className="header">
      <img src={logo} alt="Website Logo" className="logo" />
      <h1 className="website-name">YummyRoute</h1><br/>
      <p className="quote">"Your next meal is just a Click Away"</p> {/* Added quote */}
    </header>
      <h2>Menu</h2>
      <ul className="food-list">
        {foodData.map((food) => (
          <li key={food.id} className="food-item">
            <img src={food.imageUrl} alt={food.name} className="food-image" />
            <div className="food-details">
              <h3>{food.name}</h3>
              <p>Price: ${food.price}</p>
              <button onClick={() => addToCart(food)}>Add to Cart</button>
            </div>
          </li>
        ))}
      </ul>
      <h2>Your Cart</h2>
      <ul className="cart-list">
        {cart.map((item, index) => (
          <li key={index} className="cart-item">
            {item.name} - ${item.price}
          </li>
        ))}
      </ul>
      <h3>Total: ${totalAmount}</h3>
      <button onClick={onCheckout} disabled={cart.length === 0}>
        Checkout
      </button>
    </div>
  );
};

export default FoodDisplay;
